import React from "react";

export default function Die(props) {
    // initial die face
    let dieFace = "";
    switch (props.value) {
        case 1:
            dieFace = "public/images/dice1.png";
            break;
        case 2:
            dieFace = "public/images/dice2.png";
            break;
        case 3:
            dieFace = "public/images/dice3.png";
            break;
        case 4:
            dieFace = "public/images/dice4.png";
            break;
        case 5:
            dieFace = "public/images/dice5.png";
            break;
        case 6:
            dieFace = "public/images/dice6.png";
            break;
        default:
            break;
    }

    const styles = {
        backgroundImage: props.isHeld ? "#59E391" : `url(${dieFace})`,
    }
    return (
        <div className="die-face" style={styles} onClick={props.holdDice}>
            <h2 className="die-num">{props.value}</h2>
        </div>
    )
}
